package chapter2;

public class Ex03_Boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Boolean 자료형, 논리자료형 : true(0이외의 숫자), false(0) 를 저장하는 자료형
		boolean boo1 = true;
		boolean boo12 = false;
		System.out.println(boo1);
		System.out.println(boo12);
		boolean num1 = 1>2; //false
		System.out.println(num1);
		boolean num2 = 2>1; //true
		System.out.println(num2);
	

}
}