package con2;

public class Ex1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 몇줄 출력할지 설정하는 반복문
		for( int i = 1; i <= 5; i++) {
//			별을 몇개 출력할지 설정하는 반복문
			for (int j = 0; j < i; j++) {
				System.out.print("*");
		}
//			한줄을 모두 출력한 후 엔터를 치는 명령어
			System.out.println();
			
			
			
		}
		
	}
}



